import React, { useEffect } from 'react'
import { useNavigate } from 'react-router-dom';

function useCheckToken() {
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {navigate('/auth/login')};
    }, []);

  return null;
}

export default useCheckToken
